async function loadProjects(){try{const e=await fetch("/src/api/projects.php?active=true"),t=await e.json();if(!t.success||!t.data.projects)return void console.error("Failed to load projects");const r=t.data.projects,n=document.querySelector(".additional-projects .projects-grid");if(!n)return void console.error("Projects container not found");n.innerHTML="",r.forEach(e=>{const t=createProjectCard(e);n.appendChild(t)}),observeProjectAnimations()}catch(e){console.error("Error loading projects:",e)}}function createProjectCard(e){const t=document.createElement("div");t.className="project-card";const r=e.technologies?e.technologies.split(/[,\n]/).map(e=>e.trim()).filter(e=>e):[];return t.innerHTML=`\n        ${e.image_url?`\n            <div class="project-image">\n                <img src="${e.image_url}" alt="${e.title}" loading="lazy" onerror="this.src='assets/images/project-placeholder.jpg'">\n                ${e.featured?'<div class="featured-badge"><i class="fas fa-star"></i> Featured</div>':""}\n            </div>\n        `:""}\n        <div class="project-content">\n            <h4>${e.title}</h4>\n            ${e.category?`<span class="project-category">${e.category}</span>`:""}\n            <p>${e.short_description||e.description}</p>\n            ${r.length>0?`\n                <div class="project-tech">\n                    ${r.map(e=>`<span class="tech-tag">${e}</span>`).join("")}\n                </div>\n            `:""}\n            <div class="project-links">\n                ${e.demo_url?`\n                    <a href="${e.demo_url}" class="project-link" target="_blank" rel="noopener noreferrer">\n                        <i class="fas fa-external-link-alt"></i> View Demo\n                    </a>\n                `:""}\n                ${e.github_url?`\n                    <a href="${e.github_url}" class="project-link" target="_blank" rel="noopener noreferrer">\n                        <i class="fab fa-github"></i> View Code\n                    </a>\n                `:""}\n            </div>\n        </div>\n    `,t}function observeProjectAnimations(){const e=document.querySelectorAll(".project-card"),t=new IntersectionObserver(e=>{e.forEach(e=>{e.isIntersecting&&(e.target.style.opacity="1",e.target.style.transform="translateY(0)")})},{threshold:.1});e.forEach((e,r)=>{e.style.opacity="0",e.style.transform="translateY(30px)",e.style.transition=`opacity 0.5s ease ${.1*r}s, transform 0.5s ease ${.1*r}s`,t.observe(e)})}"loading"===document.readyState?document.addEventListener("DOMContentLoaded",loadProjects):loadProjects();